export function CotizarComfirmation(props) {
  useEffect(() => {
    const calculateTotalPrice = () => {
      return dummyproducts.reduce((total, product) => {
        const quantity = quantities[product.product_code];
        return total + product.product_price * quantity;
      }, 0);
  };
  setTotalPrice(calculateTotalPrice());
}, [dummyproducts, quantities]);    
    return (
      <span>
        <h2>Confirmation Information</h2>
        <p>Name: {props.name}</p>
        <p>Last Name: {props.lastName}</p>
        <p>Date: {props.date}</p>
        <p>Address: {props.address}</p>
  
        <h2>Product Information</h2>
        <table>
          <thead>
            <tr>
              <th>Product Name</th>
              <th>Product Price</th>
              <th>Quantity</th>
              <th>Total Cost</th>
            </tr>
          </thead>
          <tbody>
            {products_code.map((product) => (
              <tr key={product_code}>
                <td>{product_name}</td>
                <td>${product.product_price}</td>
                <td>{props.quantities[product.product_code] || 0}</td>
                <td>${(props.quantities[product.product_code] || 0) * product.product_price}</td>
              </tr>
            ))}
          </tbody>
        </table>
  
        <p>Total Cost: ${props.totalPrice}</p>
      </span>
    );
  };
