import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import {useForm} from 'react-hook-form'
import { CotizarComfirmation } from '../components/CotizarConfirmation';


export function CreateCotizar() {
    const [showConfirmmar, setShowConfirmmar] = useState(true);

    const toggleComponent = () => {
        setShowConfirmmar(!showConfirmmar);
    };
    const dummyproducts = [
      { product_code: 1, product_name: 'Product 1', product_price: 10 },
      { product_code: 2, product_name: 'Product 2', product_price: 15 },
      { product_code: 3, product_name: 'Product 3', product_price: 20 },
    ];
  
    const initialQuantities = dummyproducts.reduce(
      (quantities, product) => ({ ...quantities, [product.product_code]: 1 }),
      {}
    );
  
    const [quantities, setQuantities] = useState(initialQuantities);
    const [totalPrice, setTotalPrice] = useState(0);
  
    useEffect(() => {
      const calculateTotalPrice = () => {
        return dummyproducts.reduce((total, product) => {
          const quantity = quantities[product.product_code];
          return total + product.product_price * quantity;
        }, 0);
      };
  
      setTotalPrice(calculateTotalPrice());
    }, [dummyproducts, quantities]);
  
    const handleQuantityChange = (product_code, quantity) => {
      const updatedQuantities = { ...quantities, [product_code]: quantity };
      setQuantities(updatedQuantities);
      console.log(quantities)
    };
  return (
    <div className="Switch">
        {showConfirmmar ? (
            <div>
        <Table striped bordered hover>
        <thead>
        <tr>
          <th>Product Name</th>
          <th>Product Price</th>
          <th>Quantity</th>
          <th>Total Cost</th>
        </tr>
      </thead>
      <tbody>
        {dummyproducts.map((product) => (
          <tr key={product.product_code}>
            <td>{product.product_name}</td>
            <td>${product.product_price}</td>
            <td>
              <input type="number" min= '0' value={quantities[product.product_code]} onChange={(e) => handleQuantityChange(product.product_code, parseInt(e.target.value, 10))}/>
                        </td>
                        <td>${(quantities[product.code] || 0) * product.product_price}</td>
                    </tr>
                    ))}
                </tbody>
            </Table>
            <p>totalPrice</p>
        <button onClick={toggleComponent}>Toggle Component</button>
        </div>
      ) 
      : (<CotizarComfirmation />)}
    </div>
  );
}
