export function CotizarComfirmation(name, lastName, date, address, totalPrice, {products}, {quantities}) {
    const handleSubmit = (e) => {
      e.preventDefault();
      const formData = {
        name,
        lastName,
        date,
        address,
        totalPrice,
      };
    };
    return (
      <div>
        <h2>Confirmation Information</h2>
        <p>Name: {name}</p>
        <p>Last Name: {lastName}</p>
        <p>Date: {date}</p>
        <p>Address: {address}</p>
  
        <h2>Product Information</h2>
        <table>
          <thead>
            <tr>
              <th>Product Name</th>
              <th>Product Price</th>
              <th>Quantity</th>
              <th>Total Cost</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product.product_code}>
                <td>{product.product_name}</td>
                <td>${product.product_price}</td>
                <td>{quantities[product.product_code] || 0}</td>
                <td>${(quantities[product.product_code] || 0) * product.product_price}</td>
              </tr>
            ))}
          </tbody>
        </table>
  
        <p>Total Cost: ${totalPrice}</p>
      </div>
    );
  };
